import{getNormalizedRect}from"./utils/getNormalizedRect.js";export function getIntersection(t,e){const i=getNormalizedRect(t),o=getNormalizedRect(e),h=Math.max(i.left,o.left),l=Math.min(i.left+i.width,o.left+o.width);if(l<=h)return null;const r=Math.max(i.top,o.top),n=Math.min(i.top+i.height,o.height+o.top);return n<=r?null:{width:l-h,height:n-r,left:h,top:r,right:l,bottom:n}}