import{getNormalizedRect}from"./utils/getNormalizedRect.js";export function getIntersection(t,...e){const o={...getNormalizedRect(t),right:0,bottom:0};for(const t of e){const e=getNormalizedRect(t),i=Math.max(o.left,e.left),h=Math.min(o.left+o.width,e.left+e.width);if(h<=i)return null;const r=Math.max(o.top,e.top),l=Math.min(o.top+o.height,e.height+e.top);if(l<=r)return null;o.left=i,o.top=r,o.width=h-i,o.height=l-r}return o.right=o.left+o.width,o.bottom=o.top+o.height,o}