import{getStyle}from"./getStyle.js";import{getContainingBlock}from"./getContainingBlock.js";import{isWindow}from"./utils/isWindow.js";export function getPositionRoot(t,n){const o=getStyle(t,"display");if("none"===o||"contents"===o)return null;switch(n||(n=getStyle(t,"position")),n){case"relative":return t;case"fixed":return getContainingBlock(t,n);case"absolute":{const o=getContainingBlock(t,n);return isWindow(o)?t.ownerDocument:o}default:return null}}